import React, { useEffect, Fragment } from "react";
import PostsCatalog from "../components/PostsCatalog";
import { useParams } from "react-router-dom";
import { useHttpClient } from "../../global/components/front-end-comps/http";
import { useState } from "react";
import { CommonLoading } from "react-loadingg";

const UserPosts = (props) => {
  const { isLoading, error, sendRequest, clearError } = useHttpClient();
  const [loadedPost, setLoadedPost] = useState().userId;

  const userId = useParams().userId;

  useEffect(async () => {
    const fetchPosts = async () => {
      try {
        const resData = await sendRequest(
          `http://localhost:5001/api/posts/user/${userId}`
        );
        setLoadedPost(resData.posts);
      } catch (err) {}
    };
    fetchPosts();
  }, [sendRequest, userId]);

  if (isLoading) {
    return <CommonLoading color="grey" />;
  }

  return (
    <Fragment>
      {" "}
      <PostsCatalog bits={loadedPost} />{" "}
    </Fragment>
  );
};

export default UserPosts;
