import React, { useState } from "react";
import "./NewPost.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";

const NewPost = () => {
  const [location, setLocation] = useState();
  const [description, setDescription] = useState();

  const postSubmitHandler = (e) => {
    e.preventDefault();
    const result = {
      location,
      description,
    };
    console.log(result);
  };

  return (
    <CardCatalog className="post-form">
      <MDBContainer>
        <MDBRow>
          <MDBCol>
            <form onSubmit={postSubmitHandler}>
              <p className="h5 mb-4">Create new post here:</p>
              <div className="grey-text">
                <MDBInput
                  id="location"
                  label="Location"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                  onChange={(e) => setLocation(e.target.value)}
                />
                <MDBInput
                  id="description"
                  className="post-desc"
                  label="Description"
                  group
                  type="textarea"
                  validate
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>
              <div className="text-center rounded-0">
                <MDBBtn type="submit">POST</MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </CardCatalog>
  );
};

export default NewPost;
