import React, { useState, Fragment, useContext } from "react";
import "./Auth.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import UserContext from "../../global/context/UserContext";

const Auth = () => {
  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [passwordCheck, setPasswordCheck] = useState();
  const [isLogin, setIsLogin] = useState(true);
  const history = useHistory();

  const { setUserData } = useContext(UserContext);

  const authSubmitHandler = async (e) => {
    e.preventDefault();
    const newUser = {
      name,
      email,
      password,
      passwordCheck,
    };
    await axios.post("http://localhost:5001/api/users/signup", newUser);
    const loginRes = await axios.post("http://localhost:5001/api/users/login", {
      email,
      password,
    });
    setUserData({
      token: loginRes.data.token,
      user: loginRes.data.user,
    });
    localStorage.setItem("auth-token", loginRes.data.token);
    history.push("/");
  };

  const switcherHandler = () => {
    setIsLogin((prevMode) => !prevMode);
  };

  return (
    <Fragment>
      <CardCatalog className="log-form logSign">
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <form className="needs-validation" onSubmit={authSubmitHandler}>
                <p className="h5 mb-5 text-center">
                  {isLogin ? "Login" : "Sign up"}
                </p>
                <div className="grey-text">
                  {!isLogin && (
                    <MDBInput
                      id="name"
                      label="Name"
                      group
                      icon="user"
                      type="text"
                      required
                      error="wrong"
                      success="right"
                      onChange={(e) => setName(e.target.value)}
                    />
                  )}
                  <MDBInput
                    id="email"
                    label="Email"
                    group
                    icon="envelope"
                    type="email"
                    required
                    error="wrong"
                    success="right"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <MDBInput
                    id="password"
                    label="Password"
                    icon="lock"
                    group
                    required
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  {!isLogin && (
                    <MDBInput
                      id="passwordCheck"
                      label="Confirm Password"
                      icon="lock"
                      group
                      required
                      type="password"
                      onChange={(e) => setPasswordCheck(e.target.value)}
                    />
                  )}
                </div>
                {!isLogin && (
                  <MDBCol md="" className="mb-3">
                    <div className="custom-control custom-checkbox pl-3">
                      <input
                        className="custom-control-input"
                        type="checkbox"
                        value=""
                        id="invalidCheck"
                        required
                      />
                      <label
                        className="custom-control-label"
                        htmlFor="invalidCheck"
                      >
                        Agree to terms and conditions
                      </label>
                      <div className="invalid-feedback">
                        You must agree before submitting.
                      </div>
                    </div>
                  </MDBCol>
                )}
                <div className="text-center">
                  <MDBBtn type="submit" className="rounded-0">
                    {isLogin ? "LOGIN" : "SIGN UP"}
                  </MDBBtn>
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </CardCatalog>
      <CardCatalog className="log-form text-center my-4">
        <p>
          {isLogin ? "Don't have an account?" : "Have an account?"}{" "}
          <Link to="/auth" onClick={switcherHandler}>
            {isLogin ? "Sign up" : "Login"}
          </Link>
        </p>
      </CardCatalog>
    </Fragment>
  );
};

export default Auth;
