import React, { useState, Fragment } from "react";
import "./Auth.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";

const Auth = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const authSubmitHandler = (e) => {
    e.preventDefault();
    const result = {
      email,
      password,
    };
    console.log(result);
  };

  return (
    <Fragment>
      <CardCatalog className="log-form">
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <form className="needs-validation" onSubmit={authSubmitHandler}>
                <p className="h5 mb-5 text-center">Login</p>
                <div className="grey-text">
                  <MDBInput
                    id="email"
                    label="Email"
                    group
                    icon="envelope"
                    type="email"
                    required
                    // validate
                    error="wrong"
                    success="right"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <MDBInput
                    id="password"
                    label="Password"
                    icon="lock"
                    group
                    required
                    type="password"
                    // validate
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>
                {/* <MDBCol md="" className="mb-3">
                <div className="custom-control custom-checkbox pl-3">
                  <input
                    className="custom-control-input"
                    type="checkbox"
                    value=""
                    id="invalidCheck"
                    required
                  />
                  <label
                    className="custom-control-label"
                    htmlFor="invalidCheck"
                  >
                    Agree to terms and conditions
                  </label>
                  <div className="invalid-feedback">
                    You must agree before submitting.
                  </div>
                </div>
              </MDBCol> */}
                <div className="text-center">
                  <MDBBtn type="submit" className="rounded-0">
                    LOGIN
                  </MDBBtn>
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </CardCatalog>
      <CardCatalog>
        <p>Don't have an account?</p>
        <a href="/">Sign up</a>
      </CardCatalog>
    </Fragment>
  );
};

export default Auth;
