import React, { useState, Fragment } from "react";
import "./PostBit.css";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";
import Button from "../../global/components/form-comps/Button";
import Modal from "../../global/components/front-end-comps/Modal";
import Map from "../../global/components/front-end-comps/Map";

const PostBit = (props) => {
  const [openMap, setOpenMap] = useState(false);
  const [openConfirm, setOpenConfirm] = useState(true);

  const openMapHandler = () => {
    setOpenMap(true);
  };

  const closeMapHandler = () => {
    setOpenMap(false);
  };

  const openDeleteHandler = () => {
    setOpenConfirm(true);
  };

  const closeDeleteHandler = () => {
    setOpenConfirm(false);
  };

  const submitDeleteHandler = () => {
    console.log("Deleted");
  };

  return (
    <Fragment>
      <Modal
        show={openMap}
        header={props.location}
        contentClass="post-bit__modal-content"
        onClick={closeMapHandler}
      >
        <div className="map-wrap">
          <Map center={props.coordinates} zoom={16} />
        </div>
      </Modal>
      <Modal
        className="modal-prompt"
        show={openConfirm}
        onClick={closeDeleteHandler}
        header="DELETE"
        footerClass="post-bit__modal-actions"
        footer={
          <Fragment>
            <hr />
            <div className="delete-footer">
              <Button inverse onClick={closeDeleteHandler}>
                CANCEL
              </Button>
              <Button danger onClick={submitDeleteHandler}>
                DELETE
              </Button>
            </div>
          </Fragment>
        }
      >
        <p style={{ padding: "0 3rem" }} className="text-center">
          Are you sure you want to delete this post? It cannot be udone after.
        </p>
      </Modal>
      <li className="post-bit">
        <CardCatalog className="post-bit__content">
          <div className="post-bit__image">
            <img src={props.image} alt={props.location} />
          </div>
          <div className="post-bit__info">
            <h3>{props.location}</h3>
            <p>{props.description}</p>
          </div>
          <div className="post-bit__actions">
            <Button inverse onClick={openMapHandler}>
              VIEW ON MAP
            </Button>
            <Button to={`/posts/${props.id}`}>EDIT</Button>
            <Button danger onClick={openDeleteHandler}>
              DELETE
            </Button>
          </div>
        </CardCatalog>
      </li>
    </Fragment>
  );
};

export default PostBit;
