import React, { useState, Fragment } from "react";
import "./Auth.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import ErrorNotice from "../../global/components/front-end-comps/ErrorNotice";

const Login = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [error, setError] = useState();
  const history = useHistory();

  const loginSubmitHandler = async (e) => {
    e.preventDefault();
    // login
    try {
      const loginUser = { email, password };
      const loginRes = await axios.post(
        "http://localhost:5001/api/users/login",
        loginUser
      );
      setUserData({
        token: loginRes.data.token,
        user: loginRes.data.user.id,
      });
      sessionStorage.setItem("creator", loginRes.data.user.id);
      sessionStorage.setItem("token", loginRes.data.token);
      sessionStorage.setItem("name", loginRes.data.user.name);
    } catch (err) {
      err.response.data.msg && setError(err.response.data.msg);
    }
    history.push("/users");
  };

  return (
    <Fragment>
      <CardCatalog className="log-form">
        <MDBContainer>
          <MDBRow>
            <MDBCol>
              <form className="needs-validation" onSubmit={loginSubmitHandler}>
                <p className="h5 mb-5 text-center">Login</p>
                {error && (
                  <ErrorNotice
                    message={error}
                    clearError={() => setError(undefined)}
                    className="text-center"
                  />
                )}
                <div className="grey-text">
                  <MDBInput
                    id="email"
                    label="Email"
                    group
                    icon="envelope"
                    type="email"
                    required
                    error="wrong"
                    success="right"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <MDBInput
                    id="password"
                    label="Password"
                    icon="lock"
                    group
                    required
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>

                <div className="text-center">
                  <MDBBtn type="submit" className="rounded-0" color="elegant">
                    LOGIN
                  </MDBBtn>
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </CardCatalog>
      <CardCatalog className="log-form log2">
        <p>
          Don't have an account? <Link to="/signup">Sign up</Link>
        </p>
      </CardCatalog>
    </Fragment>
  );
};

export default Login;
