import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";

const DUMMY_POSTS = [
  {
    id: "p1",
    imageUrl:
      "https://tanglesandtravels.com/wp-content/uploads/2016/08/Sunset-in-Calatagan-Batangas_Small.jpg",
    address: "Binubusan, Lian, Batangas",
    description:
      "Ang magandang kabundukin at karagatan dito sa Matuod, Binubusan.",
    coordinates: {
      lat: 13.9634176,
      lng: 120.6374395,
    },
    creator: "u1",
  },
  {
    id: "p2",
    imageUrl:
      "https://a0.muscache.com/im/pictures/1fd1596d-1ef4-45d6-82c1-866915b50945.jpg?aki_policy=x_large",
    address: "Binubusan, Lian, Batangas",
    description:
      "Ang magandang kabundukin at karagatan dito sa Matuod, Binubusan.",
    coordinates: {
      lat: 13.9634176,
      lng: 120.6374395,
    },
    creator: "u2",
  },
];

const UpdatePost = () => {
  const [description, setDescription] = useState("");

  const postId = useParams().postId;

  const foundPost = DUMMY_POSTS.find((p) => p.id === postId);

  const postUpdateHandler = (e) => {
    e.preventDefault();
    const result = {
      description,
    };
    console.log(result);
  };

  if (!foundPost) {
    return (
      <div className="center">
        <h2>Cannot find your post.</h2>
      </div>
    );
  }

  return (
    <CardCatalog className="post-form">
      <MDBContainer>
        <MDBRow>
          <MDBCol>
            <form onSubmit={postUpdateHandler}>
              <p className="h5 mb-5">Edit description here:</p>
              <div className="grey-text">
                <MDBInput
                  id="description"
                  className="post-desc"
                  label="Description"
                  group
                  type="textarea"
                  validate
                  onChange={(e) => setDescription(e.target.value)}
                  value={foundPost.description}
                />
              </div>
              <div className="text-center rounded-0">
                <MDBBtn className="rounded-0" color="elegant" type="submit">
                  SAVE CHANGES
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </CardCatalog>
  );
};

export default UpdatePost;
