import React, { useEffect, Fragment } from "react";
import PostsCatalog from "../components/PostsCatalog";
import { useParams, Link } from "react-router-dom";
import { useState } from "react";
import { CommonLoading } from "react-loadingg";
import { useHttp } from "../../global/components/front-end-comps/http";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";
import { MDBBtn } from "mdbreact";

const UserPosts = () => {
  const [loadedPosts, setLoadedPosts] = useState();
  const { isLoading, sendRequest } = useHttp();

  const userId = useParams().userId;

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const resData = await sendRequest(
          `http://localhost:5001/api/posts/user/${userId}`
        );
        setLoadedPosts(resData);
      } catch (err) {}
    };
    fetchPosts();
  }, [sendRequest, userId]);

  if (loadedPosts === 0) {
    return (
      <div className="post-catalog center">
        <CardCatalog className="px-5">
          <h3 className="py-3">No posts to show.</h3>
          <Link to="/posts/new">
            <MDBBtn type="submit" className="rounded-0" color="elegant">
              Create Post
            </MDBBtn>
          </Link>
        </CardCatalog>
      </div>
    );
  }

  if (isLoading) {
    return <CommonLoading color="grey" />;
  }

  return (
    <Fragment>
      {" "}
      {!isLoading && loadedPosts && <PostsCatalog bits={loadedPosts} />}
    </Fragment>
  );
};

export default UserPosts;
