import React from "react";
import "./UserBit.css";
import Avatar from "../../shared/components/front-end-comps/Avatar";

const UserItem = (props) => {
  return (
    <li className="user-bit">
      <div className="user-bit__content">
        <div className="user-bit__image">
          <Avatar image={props.image} />
        </div>
        <div className="user-bit__info">
          <h2>{props.name}</h2>
          <h3>
            {props.postCount} {props.postCount === 1 ? "Post" : "Posts"}
          </h3>
        </div>
      </div>
    </li>
  );
};

export default UserItem;
