import React, { useState, useEffect } from "react";
import "./NewPost.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import CardCatalog from "../../global/components/front-end-comps/CardCatalog";

const NewPost = () => {
  const [address, setAddress] = useState("");
  const [description, setDescription] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const postSubmitHandler = (e) => {
    e.preventDefault();
    const apiOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        address,
        description,
      }),
    };
    useEffect(() => {
      fetch("http://localhost:5001/api/posts", apiOptions);
    });
  };

  if (isLoading) {
    return <CommonLoading />;
  }

  return (
    <CardCatalog className="post-form">
      <MDBContainer>
        <MDBRow>
          <MDBCol>
            <form onSubmit={postSubmitHandler}>
              <p className="h5 mb-4">Create new post here:</p>
              <div className="grey-text">
                <MDBInput
                  id="address"
                  label="Address"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                  onChange={(e) => setAddress(e.target.value)}
                />
                <MDBInput
                  id="description"
                  className="post-desc"
                  label="Description"
                  group
                  type="textarea"
                  validate
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>
              <div className="text-center rounded-0">
                <MDBBtn type="submit" className="rounded-0" color="elegant">
                  POST
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </CardCatalog>
  );
};

export default NewPost;
